//Importação
import './App.css';

import { Component } from 'react';

import { PostCard } from './components/PostCard';
import {loadPosts} from './utils/load-posts';

class App extends Component{

  state = {
    image: [],
    posts: []
  };

  componentDidMount(){
    this.loadPosts();
  }

  loadPosts = async() => {
    const postAndImage = await loadPosts();
    this.setState({ posts: postAndImage });
  }

  render(){
    const {posts} = this.state;

    return (
      <section className="container">
        <div className="posts"> 
          {posts.map(posts => (
            <PostCard 
              key={posts.id}
              title={posts.title}
              body={posts.body}
              id={posts.id}
              cover={posts.cover}
            />
          ))}
        </div>
      </section>


  );
  }
}

//Componente necessita começar com letra maiuscula
//App == Componente

//Exporta o Componente App
export default App;
