//Importação
import './App.css';
import { Component } from 'react';

class App extends Component{

  state = {
    posts: []
  };

  timeoutUpdate = null;

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/posts')
      .them(response => response)
  }


  render(){
    const {posts} = this.state;

    return (
    <div className="App"> 
      {posts.map(posts => (
        <div key={posts.id}>
          <h1>{posts.title}</h1>
          <p>{posts.body}</p>
        </div>
      ))}
    </div>
  );
  }
}

//Componente necessita começar com letra maiuscula
//App == Componente

//Exporta o Componente App
export default App;
