//Importação
import './App.css';
import { Component } from 'react';

class App extends Component{

  state = {
    counter: 0,
    posts: [
      {
        id: 1,
        title: 'Postagem 1',
        body: 'Corpo 1'
      },
      {
        id: 2,
        title: 'Postagem 2',
        body: 'Corpo 2'
      },
      {
        id: 3,
        title: 'Postagem 3',
        body: 'Corpo 3'
      }

    ]
  };

  componentDidMount(){
    this.handleTimeOut();
  }

  componentDidUpdate(){
    this.handleTimeOut();
  }

  handleTimeOut = () => {
    const {posts, counter} = this.state;
    posts[0].title = 'Titulo';
    setTimeout(() => {
      this.setState({posts, counter: counter + 1})
    }, 1000);
  }

  render(){
    const {posts, counter} = this.state;



    return (
    <div className="App"> 
      <h1>{counter}</h1>
      {posts.map(posts => (
        <div key={posts.id}>
          <h1>{posts.title}</h1>
          <p>{posts.body}</p>
        </div>
      ))}
    </div>
  );
  }
}

//Componente necessita começar com letra maiuscula
//App == Componente

//Exporta o Componente App
export default App;
