//Importação
import './App.css';
import { Component } from 'react';

class App extends Component{

  state = {
    image: [],
    posts: []
  };

  componentDidMount(){
    this.loadPosts();
  }

  loadPosts = async() => {
    const postResponse = fetch('https://jsonplaceholder.typicode.com/posts');
    const imageResponse = fetch('https://jsonplaceholder.typicode.com/photos');
    const [post] = await Promise.all([postResponse]);
    const [image] = await Promise.all([imageResponse]);
    const postJson = await post.json();
    const imageJson = await image.json();
    this.setState({ posts: postJson, image: imageJson });
  }

  render(){
    const {posts} = this.state;

    return (
      <section className="container">
        <div className="posts"> 
          {posts.map(posts => (
            <div className = "post-card">
              <div key={posts.id} className="post-content">
                <h1>{posts.title}</h1>
                <p>{posts.body}</p>
              </div>
            </div>

          ))}
        </div>
      </section>


  );
  }
}

//Componente necessita começar com letra maiuscula
//App == Componente

//Exporta o Componente App
export default App;
