import './App.css';
import { Component } from 'react';

class App extends Component{

  constructor(props){
    super(props);

    this.handlePClick = this.handlePClick.bind(this);

    this.state = {
      name: 'João Pedro',
      age: 18,
      counter: 0
    };
  }

  //Ao mudar o estado, a funcao render será chamada novamente
  handlePClick(){
    this.setState({name: 'junior'});
  }

  handleAClick = () => {
    const {counter} = this.state;
    const nextCounter = counter + 1;
    this.setState({counter: nextCounter});
  }

  render(){

    const {name} = this.state;
    const {age} = this.state;

    return (
    //class é palavra reservada do javaScript, logo tem que ser className
    //entre {} temos um codigo em JS
