    <div className="App"> 
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p onClick = {this.handlePClick}>
          {name} and {age} counter: {counter}
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Link
        </a>
      </header>
    </div>
  );
  }
}

